using UnityEngine;
using System.Collections;

public class UnityGui : MonoBehaviour {

  public static UnityGui Instantiate()
  {
    GameObject gui = GameObject.Find("DriveBar");
    UnityGui guiscript = gui.GetComponent<UnityGui>() as UnityGui;
    return guiscript;
  }
